! function(n) {
	n(document).ready(function() {
		n("#lead-content").fadeIn(3000);
	}), n(".top").click(function() {
        n("html, body").animate({
            scrollTop: 0
        }, 500)
    }), n("#lead-down span").click(function() {
        var e = n("#lead").next().offset().top;
        n("html, body").animate({
            scrollTop: e + "px"
        }, 500)
    })
}(jQuery);


function fIn(element) {
    var opa = 0.1; 
    element.style.display = 'block';
    var timer = setInterval(function () {
        if (opa >= 1){
            clearInterval(timer);
        }
        element.style.opacity = opa;
        opa += opa * 0.1;
    }, 100);
}

