function word(destination, speed, j){
    var words = [
      'Developer', 'Hacker', 'Programmer'
      ]
    var message = words[j % words.length];
    var i = 0;
    var interval = setInterval(function(){
        document.getElementById(destination).innerHTML += message.charAt(i);
        i++;
        // console.log("b" + i);
        if (i > message.length){
            clearInterval(interval);
            var inter = setInterval(function(){
                i--;
                document.getElementById(destination).innerHTML = document.getElementById(destination).innerHTML.replace(message, message.substr(0, i));
				message = message.substr(0, i)
                // console.log("a" + i);
                if (i < 0){
                    i = 0;	
                    document.getElementById(destination).innerHTML = document.getElementById(destination).innerHTML.replace(message, '');
                    message = words[(++j) % words.length];
					clearInterval(inter);
                    window.setTimeout(function() { word("letter", 150, ++j) }, 200);
                }
            }, 100);
        }
    }, speed);
 }

window.onload = fIn(document.getElementById("lead-content"));
window.onload = function(){
   setTimeout(function() { word("letter", 150, 0) }, 1200)
};